import java.util.*;
class Coin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  Scanner obj=new Scanner(System.in);
	        int n=obj.nextInt();
	        if(n%2==0)
	        {
	            System.out.println("Even");
	        }
	        else
	        {
	            System.out.println("Odd");
	        }
	}
}